<script type="text/javascript" language="javascript">


	var geocoder;
	var map;

	function initialize() {

		geocoder = new google.maps.Geocoder();

		var latlng = new google.maps.LatLng(-34.397, 150.644, 17);

		var myOptions = {

			zoom: 8,
			center: latlng,
			mapTypeId: google.maps.MapTypeId.ROADMAP
		}
		if (document.getElementById("map") != null) {
			map = new google.maps.Map(document.getElementById("map"), myOptions);
			codeAddress();
		}
		google.maps.event.trigger(map, 'resize');
		map.setZoom( map.getZoom() );

	}


	function decode_utf8( s )
	{

		return unescape( s );
	}

	function codeAddress() {

		var address = decode_utf8("Αρχοντάκη 3, 73100 Χανιά");
		var description = decode_utf8("<span><b>Efzin</b></span><br/><br/><span>Διευθυνση: </span> <span> Αρχοντάκη 3, 73100 Χανιά</span><br /><span>Tηλ: </span> <span>28210-28141 , 27845  </span><br /><span>Φαξ: </span> <span>28210-28141  </span><br /> <span>mail: info@efzin-property.gr </span><br/><span>Website: </span> <span>www.efzin-property.gr </span><br />");
		var type = document.getElementById("ctl00_CPHcontentRightBottom_OpenGoogleMap1_TbType").value;


		if (address=="Αρχοντάκη 3, 73100, Χανιά"){
			initialLocation = new google.maps.LatLng(37.994421,23.758596);
			map.setCenter(initialLocation);



			var point = new google.maps.LatLng(37.994421,23.758596);
			var marker2 = new google.maps.Marker({
				map: map,
				position: point
			});
			var contentString = "<div style='color:black;'><br/>" + description + "<br/></div>"
			var infowindow = new google.maps.InfoWindow({
				content: contentString
			});

			google.maps.event.addListener(marker2, 'click', function() {
				infowindow.open(map, marker2);
			});
			infowindow.open(map, marker2);
			map.setZoom(16);

		}
		else{


			if (geocoder) {
				geocoder.geocode({ 'address': address }, function(results, status) {
					if (status == google.maps.GeocoderStatus.OK) {
						map.setCenter(results[0].geometry.location);
						var marker = new google.maps.Marker({
							icon:"./App_themes/Mytheme/Images/markers.png",
							shadow:"./App_themes/Mytheme/Images/marker_shadow.png",
							map: map,
							position: results[0].geometry.location
						});


						var contentString = "<div style='color:black;'><br/>" + description + "<br/></div>"
						var infowindow = new google.maps.InfoWindow({
							content: contentString
						});

						google.maps.event.addListener(marker, 'click', function() {
							infowindow.open(map, marker);
						});
						infowindow.open(map, marker);


						var lngSpan = results[0].geometry.location.lng();
						var latSpan = results[0].geometry.location.lat();

						if (0!= "0"){
							for (var i = 0; i < 0; i++) {
								var num  = -0.01000 + (0.01000-(-0.01000))*Math.random();
								var num2 = -0.01000 + (0.01000-(-0.01000))*Math.random();
								num= num.toFixed(6);
								num2= num2.toFixed(6);


								var point = new google.maps.LatLng(parseFloat(latSpan) + parseFloat(num),parseFloat(lngSpan) + parseFloat(num2));
								var marker1 = new google.maps.Marker({
									map: map,
									position: point
								});
							}
						}
						map.setZoom(16);
						if (type == "Nomos") {
							map.setZoom(14);
						}
					}
					else
					{

						// alert("Geocode was not successful for the following reason: " + status);
						document.getElementById("map").style.display="none";
						document.getElementById("NoMap").style.display="block";
					}
				});

			}
		}
	}

	initialize();

</script>


